    private void validateKey(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_validateKey
        // TODO add your handling code here:
        JTextField source = (JTextField) evt.getSource();
        if ((evt.getKeyChar() >= '0' && evt.getKeyChar() <= '9') || evt.getKeyCode() == 8  || (evt.getKeyCode() >=37 && evt.getKeyCode() <=40)) {
            source.setEditable(true);
        } else {
            source.setEditable(false);
        }
    }//GEN-LAST:event_validateKey

    private void ph1_generateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ph1_generateActionPerformed
        // TODO add your handling code here:
    	d1=0;
    	d2=0;
    	d3=0;
        this.jTextArea_ph1.setText("");
        JTextAreaOutputStream out = new JTextAreaOutputStream (this.jTextArea_ph1);
        System.setOut (new PrintStream (out));
        
        if(!this.carriers.getText().equalsIgnoreCase("") && !this.days.getText().equalsIgnoreCase("") && !this.shipments.getText().equalsIgnoreCase("")){
            int numberOfCarriers = Integer.parseInt(this.carriers.getText());
            int numberOfDays = Integer.parseInt(this.days.getText());
            int hourlyLimit = Integer.parseInt(this.shipments.getText());
            ShippingCompany ob = new ShippingCompany();
            if(this.ph1_type.getItemAt(this.ph1_type.getSelectedIndex()).equalsIgnoreCase("Full Report")){
                System.out.println("=====================================================[Phase#1]========================================================= ");
                ob.simulatePhase1(numberOfCarriers,numberOfDays,hourlyLimit);
                if(d3 == 0) {
                	hr1=ShippingCompany.getHourReports1();
                	d3=1;
                }
                System.out.println("=======================================================[End]=========================================================== ");                
            }
            else{
                int day_ph1 = Integer.parseInt(this.ph1_day.getItemAt(this.ph1_day.getSelectedIndex()));
                int hour_ph1 = -1;
                String value = this.ph1_hour.getItemAt(this.ph1_hour.getSelectedIndex());
                if(!value.equalsIgnoreCase("Full Day"))
                    hour_ph1 = Integer.parseInt(value);
                if(hour_ph1 == -1)
                	this.jTextArea_ph1.setText(ShippingCompany.printReports(day_ph1,1));
                else 
                {
                	this.jTextArea_ph1.setText(hr1.get(day_ph1-1).get(hour_ph1));
                }
                //ob.simulatePhase1_DailyHourly(numberOfCarriers,numberOfDays,hourlyLimit, day_ph1, hour_ph1);
                
            }
            if(d1 ==0)
            {	
            	cr1= ShippingCompany.cumluativeRecieved1;
            	cd1= ShippingCompany.cumluativeDelivered1;
            	cf1= ShippingCompany.cumluativeFailed1;
            	d1 = 1;
            }
            this.received.setText(String.valueOf(cr1));
            this.delivered.setText(String.valueOf(cd1));
            this.failed.setText(String.valueOf(cf1));
//            this.in_depo.setText(String.valueOf(ShippingCompany.shipments.size()+ShippingCompany.totalReceived));
           // this.cumulative.setText(String.valueOf(ShippingCompany.cumulativeShipments.size()));
//            this.no_handled.setText(String.valueOf(ShippingCompany.shipments.size()));
            this.avg_shipment_per_hour.setText(String.valueOf(ShippingCompany.phase1Average));
            
            this.current_day.setText(String.valueOf(ShippingCompany.curr_day));
            this.current_hour.setText(String.valueOf(ShippingCompany.curr_hour));
            this.working_carrier.setText(String.valueOf(ShippingCompany.carrierShift));
            this.ph1_summary.setText(String.valueOf(ShippingCompany.numberOfFailedPhase1));
            if(ShippingCompany.numberOfFailedPhase2 != 0){
                this.improved_pec.setText(String.valueOf(((1-(double)ShippingCompany.numberOfFailedPhase2/(ShippingCompany.numberOfFailedPhase1+ShippingCompany.numberOfFailedPhase2)))*100));
            }
//            System.out.println("=====================================================[Phase#2]========================================================= ");
//            ob.simulatePhase2(numberOfCarriers,numberOfDays,hourlyLimit);
//            System.out.println("=======================================================[End]=========================================================== ");
        }
        else JOptionPane.showMessageDialog(null, "Please insert the valid values", "Invalid Values", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_ph1_generateActionPerformed

