    private void ph1_typeItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_ph1_typeItemStateChanged
        // TODO add your handling code here:
        if(ph1_type.getItemAt(ph1_type.getSelectedIndex()).equalsIgnoreCase("Full Report")){
            this.ph1_day.setEnabled(false);
            this.ph1_hour.setEnabled(false);
        }
        else if(ph1_type.getItemAt(ph1_type.getSelectedIndex()).equalsIgnoreCase("Day/Hourly Report")){
            this.ph1_day.setEnabled(true);
            this.ph1_hour.setEnabled(true);
        }
    }//GEN-LAST:event_ph1_typeItemStateChanged

